import React from "react";

export default class EmpFrom extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      requestId: null,
      requestType: "",
      istransfer: false,
      selectedImage: null,

      empType: "",
      isContractor: false,
      transferDept: "",
      contractorDept: "",
      otherDept: "",
      otherLastDate: null,

      empId: null,
      dept: "",
      fName: "",
      lName: "",
      startDate: null,
      endDate: null,

      isHistory: false,
      purchaseRequest: "",
      system: ""
    };
  }

  imageSelectionHandle = (event) => {
    this.setState({
      selectedImage: URL.createObjectURL(event.target.files[0])
    });
  };
  imageUploadHandle = () => { };

  requestTypeHandle = (event) => {
    if (event.target.value === "Transfer  ") {
      this.setState({ istransfer: true });
    } else {
      this.setState({ istransfer: false });
    }
    this.setState({ requestType: event.target.value });
  };

  empTypeHandle = (event) => {
    if (event.target.value === "Contractor") {
      this.setState({ empType: "Contractor" });
    } else if (event.target.value === "Other") {
      this.setState({ empType: "Contracto" });
    } else {
      this.setState({ isContractor: false });
    }
    this.setState({ empType: event.target.value });
  };

  valueHandle = () => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({ [nam]: val });
  };
  submitHandle = () => {
    console.log(this.state);
  };
  historyHandle = (event) => {
    var checkBox = document.getElementById("myCheck");

    if (event.target.value == "Yes") {
      if (checkBox.checked == true) {
        this.setState({ isHistory: true });
      }
      if (checkBox.checked == false) {
        this.setState({ isHistory: false });
      }
    }
  };

  render() {
    let transfer;
    if (this.state.istransfer) {
      transfer = (
        <div>
          <label>
            Employee Transferring from CT US :
            <input type="text" name="otherCompany" />
          </label>
        </div>
      );
    }

    let empTypeOption;
    if (this.state.empType === "Contractor") {
      empTypeOption = (
        <div>
          <label>
            If Contractor , what Company They Worked for :
            <input type="text" name="otherCompany" />
          </label>
        </div>
      );
    }
    if (this.state.empType === "Other ") {
      empTypeOption = (
        <div>
          <label>
            If Other, specify :
            <input type="text" name="otherCompany" />
          </label>
        </div>
      );
    }

    let history;
    if (this.state.isHistory) {
      history = (
        <div>
          <label>
            GID/Email:
            <input type="text" name="GID" />
          </label>

          <label>
            Last Current Unit
            <input type="text" name="Comments" />
          </label>
          <br />
          <label>
            Comments
            <input type="text" name="Comments" />
          </label>
          <label>
            Last work date from resume :
            <input
              type="date"
              name="lastWorkDate"
              onChange={this.valueHandle}
            />
          </label>
        </div>
      );
    }

    let purchaseOption;
    if (this.state.purchaseRequest == "Yes") {
      purchaseOption = (
        <div>
          <label>
            Please enter the Purchase Request :
            <input type="text" name="Purchase" />
          </label>
        </div>
      );
    }

    let systemOptions;
    if (this.state.system == "Other") {
      systemOptions = (
        <div>
          <label>
            Please specify Other options :
            <input type="text" name="system" />
          </label>
        </div>
      );
    }

    return (
      <div>
        <h3>Employee Form </h3>

        <h4 className="headings">Employee Details</h4>

        <form onSubmit={this.submitHandle}>
          <div className="image">
            <label>
              <img
                src={this.state.selectedImage}
                alt="preview"
                width="150px"
                height="150px"
              />
              <br />
              <input
                type="file"
                name="image"
                onChange={this.imageSelectionHandle}
              />
              <br />
            </label>
          </div>
          <div className="Empdetails">
            <label>
              Request ID: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
              <input
                type="text"
                name="requestId"
                onChange={(e) => this.setState({ requestId: e.target.value })}
              />
            </label>
            <br />
            <br />

            <div className="Request">
              <label>
                Request Type :&nbsp;
                <input
                  type="button"
                  value="Transfer  "
                  onClick={this.requestTypeHandle}
                />
                <input
                  type="button"
                  value="New Hire"
                  onClick={this.requestTypeHandle}
                />
                <input
                  type="button"
                  value="  Re-Hire  "
                  onClick={this.requestTypeHandle}
                />
                <input
                  type="button"
                  value="Status Change"
                  onClick={this.requestTypeHandle}
                />
                <input
                  type="button"
                  value="Date Extension"
                  onClick={this.requestTypeHandle}
                />
              </label>
            </div>
            {transfer}
            <br />
            <label>
              Employee Type:
              <input
                type="button"
                value="Full Time"
                onClick={(e) => this.setState({ empType: e.target.value })}
              />
              <input
                type="button"
                value="   Intern   "
                onClick={(e) => this.setState({ empType: e.target.value })}
              />
              <input
                type="button"
                value="Contractor"
                onClick={(e) => this.setState({ empType: e.target.value })}
              />
              <input
                type="button"
                value="Other "
                onClick={(e) => this.setState({ empType: e.target.value })}
              />
            </label>
            {empTypeOption}
            <br />
            <br />

            
            <div class="form-group-row form-inline">
            
              <label >First Name :</label>
              <input type="text" class="form-control" id="name" name="fname" />
            
            
              <div class="col-xs">
                <label for="pwd">Last Name:</label>
                <input type="text" class="form-control" id="pwd" name="lname" />
              </div>

              <div class="form-group">
                <label for="pwd">Employee Id:</label>
                <input type="text" class="form-control" id="pwd" name="empId" />
              </div>
              </div>

              

                <div className="Names">
                  <label>
                    First Name :&nbsp;&nbsp;&nbsp;&nbsp;
                <input
                      type="text"
                      name="fname"
                      size="40"
                      onChange={this.valueHandle}
                    />
                  </label>

                  <label>
                    Last Name :
                <input
                      type="text"
                      name="lname"
                      size="40"
                      onChange={this.valueHandle}
                    />
                  </label>

                  <label>
                    Employee ID :
                <input
                      type="text"
                      name="empId"
                      size="40"
                      onChange={this.valueHandle}
                    />
                  </label>
                  <br />
                </div>

                <div className="Dates">
                  <label>
                    Department:&nbsp;&nbsp;&nbsp;&nbsp;
                <select width="20px">
                      <option selected value="None">
                        Select...
                  </option>
                      <option value="1">1</option>
                      <option value="2">2</option>
                      <option value="2">3</option>
                    </select>
                  </label>

                  <label>
                    From :
                <input
                      type="date"
                      name="startDate"
                      onChange={this.valueHandle}
                    />
                  </label>
                  <label>
                    Until :
                <input type="date" name="endDate" onChange={this.valueHandle} />
                  </label>
                  <br />
                </div>
                <label>
                  Comments:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
              <input type="text" name="Comment1" onChange={this.valueHandle} />
                </label>
              </div>
              <br />

              <label>
                Did this person work for Siemens Before ? : *<br />
                <input
                  type="checkbox"
                  onClick={(e) => this.setState({ isHistory: true })}
                />
                <label>Yes</label>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
            <input
                  type="checkbox"
                  onClick={(e) => this.setState({ isHistory: false })}
                />{" "}
                <label>No</label>
                {history}
              </label>

              <h4 className="headings"> Workspace Requirements</h4>
              <div className="Workspace">
                <p>Please select workspace requirements :</p>
                <p>System </p>
                <div className="system">
                  <input
                    type="checkbox"
                    onClick={(e) => this.setState({ system: "Desktop" })}
                  />
              Desktop
              <input
                    type="checkbox"
                    name="Laptop"
                    onClick={(e) => this.setState({ system: "Laptop" })}
                  />
              Laptop
              <input
                    type="checkbox"
                    onClick={(e) => this.setState({ system: "Server" })}
                  />
              Server
              <input
                    type="checkbox"
                    onClick={(e) => this.setState({ system: "Other" })}
                  />
              Other
              {systemOptions}
                </div>
                <br />
                <br />
                <label>Phone </label>
                <br />
                <div className="Phone">
                  <input type="checkbox" />
              Desk
              <input type="checkbox" />
              Mobile
              <br />
                  <br />
                </div>
              </div>
              <label>Others </label>
              <input type="text" name="PhoneOthers" />

              <label class="checkbox-inline"><input type="checkbox" value="" />Option 1</label>
<label class="checkbox-inline"><input type="checkbox" value="" />Option 2</label>
<label class="checkbox-inline"><input type="checkbox" value="" />Option 3</label>


              <h4 className="headings"> Building Access</h4>
              <div className="Building">
                <label>Work Location</label>
                <br />

                <label>Seating Location</label>
              </div>

              <input type="submit" value="Submit" />
        </form>
          </div>
    );
  }
}
